import React, { Component } from 'react';
import axios from 'axios';
import { confirmAlert } from 'react-confirm-alert'; // Import
import { Animation, MDBCarousel, MDBCarouselCaption, MDBCarouselInner, MDBCarouselItem, MDBView, MDBMask, MDBContainer } from
"mdbreact";
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
export default class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {

            nom: '',
            prenom: '',
            email: '',
            telephone: '',
            adresse: '',
            pret: '',
            remise: '',
            profil: []
        };
        this.onChange = this.onChange.bind(this)

    }
    onChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    componentDidMount() {
        axios.get('http://localhost:8080/api/users/newArticle')
            .then(response => {
                this.setState({ profil: response.data });
                console.log('profil: ', this.state.profil);

            })
            .catch(function (error) {
                console.log(error);
            })

    }

    render() {
        // let imgUrl = './image/livre6.jpg';
        return (
            <div>
                {/* <div style={{
                    backgroundImage: 'url(' + imgUrl + ')',
                    backgroundSize: 'cover', backgroundPosition: 'center center', backgroundRepeat: 'no-repeat',
                }} id="image"

                >
                    <div className="text-white  px-md-5 mx-md-5 py-5 px-4">
                        <div className="py-5"> */}
                            {/* <h2 id="h2">ATELIER DE CUISINE</h2>
                            <p  id="verdana" className="mb-4 pb-2 px-md-5 ">
                                <span>Aimez-vous cuisiner?Personnes âgés de 25 à 35 ans?</span> <br /><br />
                                Un atelier de cuisine est fait pour vous si vous aimez <br />la cuisine facile et créative !
                //             </p> */}




                <MDBCarousel activeItem={1} length={4} showControls={true} showIndicators={true} className="z-depth-1">
                <MDBCarouselInner>
                  <MDBCarouselItem itemId="1">
                    <MDBView>
                      <img className="d-block w-100 image" src="./image/livre6.jpg" alt="First slide" />
                      <MDBMask overlay="black-slight" />
                    </MDBView>
                    {/* <MDBCarouselCaption>
                      <Animation type="zoomInUp" duration="3s">
                        <h3 className="h3-responsive">"Environment is no one's property to destroy; it's everyone's responsibility to protect."</h3>
                        <p>Mohith Agadi</p>
                      </Animation>
                    </MDBCarouselCaption> */}
                  </MDBCarouselItem>
                  <MDBCarouselItem itemId="2">
                    <MDBView>

                      <img className="d-block w-100 image" src="./image/livre3.jpg" alt="Second slide" />
                      <MDBMask overlay="black-slight" />
                    </MDBView>
                    {/* <MDBCarouselCaption>
                      <Animation type="zoomInUp" duration="3s">
                        <h3 className="h3-responsive">"One touch of nature makes the whole world kin. "</h3>
                        <p>William Shakespeare</p>
                      </Animation>
                    </MDBCarouselCaption> */}
                  </MDBCarouselItem>
                  <MDBCarouselItem itemId="3">
                    <MDBView>
                      <img className="d-block w-100 image" src="./image/livre2.jpg"  width="700px" alt="Third slide" />
                      <MDBMask overlay="black-slight" />
                    </MDBView>
                    {/* <MDBCarouselCaption>
                      <Animation type="zoomInUp" duration="3s">
                        <h3 className="h3-responsive">"The earth has a skin and that skin has diseases; one of its diseases is called man."</h3>
                        <p>Friedrich Nietzsche</p>
                        </Animation>
                    </MDBCarouselCaption> */}
                  </MDBCarouselItem>
                  <MDBCarouselItem itemId="4">
                    <MDBView>
                      <img className="d-block w-100 image" src="./image/livre1.jpg" alt="Burnedforest" />
                      <MDBMask overlay="black-slight" />
                    </MDBView>
                    {/* <MDBCarouselCaption>
                      <Animation type="zoomInUp" duration="3s">
                        <h3 className="h3-responsive">"Our planet's alarm is going off, and it is time to wake up and take action!"</h3>
                        <p>Leonardo DiCaprio</p>
                      </Animation>
                    </MDBCarouselCaption> */}
                  </MDBCarouselItem>
                </MDBCarouselInner>
              </MDBCarousel>
                    <div className="row" >
                        {
                            (this.state.profil.length > 0) ? (this.state.profil.filter((params)=>params.visib).map((obj) => {
                                return <div className="col-md-4 mx-auto mt-5" key={obj._id}>
                                    <div className="card1" >
                                        <img class="card-img-top" id="sary" src={'http://localhost:8080/api/users/newArticleImage/' + obj.image} alt="Cardimagecap" />
                                        <div class="card-body">
                                            <h3 class="card-title titre1"><b>*{obj.titre}*</b></h3>
                                            {/* <h3 class="card-text titre2">{obj.description}</h3>
                                            <h3 className="card-title titre3">Le {obj.date} à {obj.debut}</h3>
                                            <h3 class="card-text titre3">Durée:{obj.duree}h</h3>
                                            <h3 class="card-text titre3">Places disponible:{obj.place}</h3>
                                            <h3 class="card-text titre3">Places réservées:{obj.placeRes}</h3> */}
                                            <h3 className="card-text titre1">Paf:{obj.prix}€ <span id="pers">/pers</span> </h3>
                                            <center>
                                                <button className="btn btn1"
                                                    onClick={() => {
                                                        confirmAlert({
                                                            customUI: ({ onClose }) => {
                                                                return (
                                                                    <div id="popup" >
                                                                        <input className="" name="nom" onChange={this.onChange} value={this.state.value} placeholder="Nom" /><br></br>
                                                                        <input name="prenom" placeholder="Prenom" onChange={this.onChange} value={this.state.value} /><br></br>
                                                                        <input name="phone" placeholder="Numero téléphone" onChange={this.onChange} value={this.state.value} /><br></br>
                                                                        <input name="email" placeholder="Email" onChange={this.onChange} value={this.state.value} /><br></br>
                                                                        <input name="prêt" placeholder="Date de prêt" onChange={this.onChange} value={this.state.value} /><br></br>
                                                                        <input name="remise" placeholder="Date de remise" onChange={this.onChange} value={this.state.value} /><br></br>
                                                                        <center>
                                                                        <button
                                                                            onClick={(e) => {
                                                                                e.preventDefault()
                                                                                axios.post("http://localhost:8080/api/users/particulier/" + obj._id, {
                                                                                    nom: this.state.nom,
                                                                                    prenom: this.state.prenom,
                                                                                    phone: this.state.phone,
                                                                                    pret: this.state.pret,
                                                                                    remise: this.state.remise,
                                                                                    email: this.state.email

                                                                                }).then(res => {
                                                                                    axios.get("http://localhost:8080/api/users/newArticle").then(res => {

                                                                                        this.setState({ profil: res.data })
                                                                                    })
                                                                                    onClose()
                                                                                })

                                                                            }} >Confirmer
                                                                        </button>
                                                                        <button onClick={onClose}>Annuler</button>
                                                                        </center>

                                                                    </div>
                                                                );
                                                            }
                                                        });
                                                    }}
                                                    id="inscrire-btn">S'inscrire</button>
                                            </center>
                                        </div>
                                    </div>
                                </div>
                            })) : ('')
                        }
                    </div>
                </div>
        );
    }
}
